<?php

namespace Sante\SpecialisteBundle\Repository;

/**
 * specialisteRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class specialisteRepository extends \Doctrine\ORM\EntityRepository
{
    function ExistanceProfil($id)
    {
        $query=$this->getEntityManager()
            ->createQuery("
    select s from SanteSpecialisteBundle:specialiste s
    WHERE s.id LIKE :id")
            ->setParameter('id','%'.$id.'%');
        $q=$query->getResult();
        if ($q == null)
        {return 1;}
        else return $q ;
    }
}
